  },
  {
    "key": "ctrl+alt+shift+d",
    "command": "editor.action.addSelectionToPreviousFindMatch",
    "when": "editorTextFocus || editorFocus"
  },
  {
    "key": "ctrl+alt+d",
    "command": "editor.action.addSelectionToNextFindMatch",
    "when": "editorTextFocus || editorFocus"
  },
