{"settings":"{
  "workbench.iconTheme": "material-icon-theme",
  "workbench.editor.untitled.hint": "hidden",
  // Editor
  "editor.bracketPairColorization.enabled": true,
  "editor.fontLigatures": false,
  "editor.fontFamily": "Hack Nerd Font",
  "editor.fontSize": 14,
  "editor.fontWeight": "normal",
  "editor.tabSize": 4,
  "editor.tabCompletion": "off",
  "editor.lineNumbers": "relative",
  "editor.quickSuggestions": {
    "other": "on",
    "comments": "off",
    "strings": true
  },
  "editor.suggestSelection": "first", //se seleciones la primera sugerencia por defecto
  "editor.acceptSuggestionOnEnter": "on",
  //
  //
  // vim
  //"vim.leader": "
  "vim.autoindent": true,
  "vim.useSystemClipboard": true,
  "vim.mouseSelectionGoesIntoVisualMode": true,
  // "vim.statusBarColors.insert": "#943126",
  // "vim.statusBarColors.normal": "#34495E",
  // "vim.statusBarColors.visual": "#7D6608",
  // "vim.statusBarColors.replace": "#F9E79F",
  // "vim.statusBarColorControl": true,
  // Comandos para Vim
  // "vim.normalModeKeyBindingsNonRecursive": [
  //   {
  //     "before": ["<C-d>"],
  //     "after": ["addSelectionToNextFindMatch"]
  //   }
  // ],
  "debug.console.fontSize": 13,
  "git.autofetch": true,
  "[python]": {
    "editor.formatOnType": true,
    "editor.defaultFormatter": "ms-python.python"
  },
  "workbench.editorAssociations": {
    "*.lnk": "sqlite-viewer.option",
  },
  "django.snippets.exclude": [
    "cms",
    "wagtail"
  ],
  //"vim.neovimConfigPath": "C:/Users/ERICK/AppData/Local/nvim/init.vim"
  // tailwind css
  "tailwindCSS.includeLanguages": {
    "plaintext": "html"
  },
  "[svg]": {
    "editor.wordWrap": "on"
  },
  "[html]": {
    // "editor.suggest.insertMode": "replace",
    "editor.wordWrap": "on",
    "editor.defaultFormatter": "vscode.html-language-features"
  },
  //
  //
  //Configuracion PABLO Monteserie
  //Usar emmet cuando estamos codificando con React
  "files.associations": {
    "*.js": "javascript",
    "*.jsx": "javascriptreact"
  },
  //Detectar HTML en una cadena de Javascript
  "emmet.syntaxProfiles": {
    "javascript": "html"
  },
  "emmet.includeLanguages": {
    "javascript": "html"
  },
  // Ir directamente a la definición de una función o variable al hacer ctrl + click sobre ella
  "editor.multiCursorModifier": "alt",
  // Salvar un fichero cuando pierda el foco
  "files.autoSave": "onFocusChange",
  // Formateado de código automático
  "editor.formatOnSave": true,
  "editor.formatOnPaste": true,
  "editor.mouseWheelZoom": true,
  "workbench.tree.indent": 10,
  "workbench.tree.renderIndentGuides": "always",
  // Añadimos una línea roja sobre la pestaña activa
  "workbench.colorCustomizations": {
    "tab.activeBorderTop": "#0aff4f",
    // color Brackets
    "editorBracketHighlight.foreground1": "#ff7272",
    "editorBracketHighlight.foreground2": "#D99018",
    "editorBracketHighlight.foreground3": "#f388ff",
    "editorBracketHighlight.foreground4": "#009dff",
    "editorBracketHighlight.foreground5": "#22c7a3",
    "editorBracketHighlight.foreground6": "#ffffff",
    // color para el suggest del Intellisense
    "editorSuggestWidget.selectedBackground": "#64830f",
    // color del la linea que estamos
    "editorLineNumber.activeForeground": "#D99018"
  },
  // colores de la variables
  // "editor.tokenColorCustomizations": {
  //   "types": "#009fdffs",
  //   "strings": "#0s39b00",
  //   "variables": "#00e5ff"
  //   "functions": "#009dff"
  // },
  "liveServer.settings.donotVerifyTags": true, // zoom del editor
  "[javascript]": {
    "editor.defaultFormatter": "esbenp.prettier-vscode"
  },
  "[jsonc]": {
    "editor.defaultFormatter": "vscode.json-language-features"
  },
  "[jsx]": {
    "editor.defaultFormatter": "esbenp.prettier-vscode"
  },
  // tranparencia del editor de 1- 255  ctrl+ alt Z ->dizminuir
  // ctrl +alt +c -> aumentar
  // "glassit.alpha": 245,
  // "markdown-preview-enhanced.liveUpdate": true,
  "editor.minimap.enabled": false,
  "editor.wordWrap": "off",
  "editor.inlineSuggest.enabled": true,
  "liveServer.settings.donotShowInfoMsg": true,
  "github.copilot.enable": {
    "*": false,
    "plaintext": false,
    "markdown": false,
    "scminput": false
  },
  "markdown-preview-enhanced.previewTheme": "github-dark.css",
  "settingsSync.ignoredExtensions": [
    "vscode.theme-quietlight"
  ],
  //  configuracion de la terminal del Vscode
  "workbench.colorTheme": "Gruvbox Material Dark",
  "terminal.integrated.fontFamily": "Hack NF",
  "terminal.integrated.fontSize": 13,
  "terminal.integrated.fontWeight": "normal",
  //
  // Formater prettier -> tabulacion de 4 sapcios
  "prettier.tabWidth": 4,
  "[javascriptreact]": {
    "editor.defaultFormatter": "esbenp.prettier-vscode"
  },
  // "[html]": {
  //   "editor.tabSize": 2
  // }
}"}
